    DROP TABLE IF EXISTS pmvn;
	CREATE TABLE IF NOT EXISTS pmvn (pmvnid integer primary key,wordid integer,synsetid integer,vnwordid integer,vnclassid integer);
	INSERT INTO pmvn (pmvnid,wordid,synsetid,vnwordid,vnclassid) SELECT pmroleid,wordid,synsetid,vnwordid,vnclassid FROM pm WHERE vnclassid IS NOT NULL;
	DELETE FROM pmvn WHERE pmvnid NOT IN (SELECT MIN(pmvnid) FROM pmvn GROUP BY wordid,synsetid,vnwordid,vnclassid);
	DROP TABLE IF EXISTS pmpb;
	CREATE TABLE IF NOT EXISTS pmpb (pmpbid integer primary key,wordid integer,synsetid integer,pbwordid integer,pbrolesetid integer);
	INSERT INTO pmpb (pmpbid,wordid,synsetid,pbwordid,pbrolesetid) SELECT pmroleid,wordid,synsetid,pbwordid,pbrolesetid FROM pm WHERE pbrolesetid IS NOT NULL;
	DELETE FROM pmpb WHERE pmpbid NOT IN (SELECT MIN(pmpbid) FROM pmpb GROUP BY wordid,synsetid,pbwordid,pbrolesetid));
	DROP TABLE IF EXISTS pmfn;
	CREATE TABLE IF NOT EXISTS pmfn (pmfnid integer primary key,wordid integer,synsetid integer,fnwordid integer,fnluid integer,fnframeid integer);
	INSERT INTO pmfn (pmfnid,wordid,synsetid,fnwordid,fnluid,fnframeid) SELECT pmroleid,wordid,synsetid,fnwordid,fnluid,fnframeid FROM pm WHERE fnframeid IS NOT NULL;
	DELETE FROM pmfn WHERE pmfnid NOT IN (SELECT MIN(pmfnid) FROM pmfn GROUP BY wordid,synsetid,fnwordid,fnluid,fnframeid);


            CREATE VIRTUAL TABLE IF NOT EXISTS words_lemma_fts4 USING fts4(wordid integer not null, lemma);
			INSERT INTO words_lemma_fts4 SELECT wordid, lemma from words;
			CREATE VIRTUAL TABLE IF NOT EXISTS synsets_definition_fts4 USING fts4(synsetid integer not null, definition);
			INSERT INTO synsets_definition_fts4 SELECT synsetid, definition from synsets;
			CREATE VIRTUAL TABLE IF NOT EXISTS samples_sample_fts4 USING fts4(synsetid integer not null, sample);
			INSERT INTO samples_sample_fts4 SELECT synsetid, sample from samples;


